import React from 'react';
import JwtService from 'app/services/jwtService';
import { Grid } from '@material-ui/core';
import MaterialTable from 'material-table';
import { useHistory } from 'react-router';
import FusePageSimple from '@fuse/core/FusePageSimple';
import { connect } from 'react-redux';

function Vendors({ user }) {
	const history = useHistory();
	async function getData(query) {
		return new Promise(async resolve => {
			JwtService.init();
			const axios = JwtService.getAxios();
			const params = {
				page: query.page,
				limit: query.pageSize,
				q: query.search
			};

			if (user.data.role === 'vendor') params.parent_id = user.data.xid;
			else params.parent_id = 0;
			const vendors = await axios.get('/api/admin/vendor', {
				params
			});
			const resp = vendors.data;
			if (resp.status) {
				resolve({
					data: resp.data.data, /// your data array
					page: query.page, // current page number
					totalCount: resp.data.total // total row number
				});
			} else {
				resolve({
					data: [], /// your data array
					page: 0, // current page number
					totalCount: 0 // total row number
				});
			}
		});
	}

	function actions() {
		const actions = [];

		if (['admin', 'it', 'creative', 'alliance'].includes(user.data.role)) {
			actions.push({
				icon: 'visibility',
				tooltip: 'View Vendor',
				onClick: (event, data) => history.push('/vendor/' + data.id+"/about")
			});
		}
		return actions;
	}

	return (
		<FusePageSimple
			content={
				<Grid container>
					<Grid item md={12}>
						<MaterialTable
							title="Vendors"
							data={getData}
							columns={[
								{
									title: 'Name',
									field: 'name',
									width: 30
								},
								{
									title: 'Locality',
									field: 'locality',
									width: 30
								},
								{
									title: 'City',
									field: 'city.name',
									width: 10
								},
								{
									title: 'Status',
									field: 'status',
									width: 10
								}
							]}
							actions={actions()}
							options={{
								pageSize: 10,
								pageSizeOptions: [10, 20, 50],
								actionsColumnIndex: -1
							}}
						/>
					</Grid>
				</Grid>
			}
		/>
	);
}
function mapStateToProps({ auth }) {
	return {
		user: auth.user
	};
}

export default connect(mapStateToProps)(Vendors);
